    def Browse_button(self, text: QtWidgets.QLineEdit):
        destDir = QtWidgets.QFileDialog.getExistingDirectory(self, 'Выбрать папку для работы программы', \
                                                             '/home/lexa', QtWidgets.QFileDialog.ShowDirsOnly)
        if text.text() != "": text.setText("")
        if destDir: text.setText(str(destDir)+"/")

    def Open_Configuration(self):
        if self.radExtension.isChecked():
            path = "/home/lexa/SortFile/file/Configuration Extension.txt" 
        elif self.radKey.isChecked():
            path = "/home/lexa/SortFile/file/Configuration Key.txt"
        else: return
        with open(path, 'r',encoding="UTF-8") as data:
            value = [string.replace("\n", "").split(" - ") for string in data]
        self.tableWidget.setRowCount(len(value))
        self.spbRow.setMaximum(len(value)+1)
        self.spbRow.setValue(len(value)+1)
        for row, elm in enumerate(value):
            self.tableWidget.setItem(row,0,QTableWidgetItem(elm[0]))
            self.tableWidget.setItem(row,1,QTableWidgetItem(elm[1]))

    def Save_Configuration(self):
        rowPosition = self.tableWidget.rowCount()
        if rowPosition == 0: return
        inf = "ключами" if self.radKey.isChecked() else "расширениями"
        message = f'Вы уверены, что хотите сохранить файл с {inf}'
        flag = CustomMessageBox().show_question_box("Подверждение", message)
        if self.radExtension.isChecked() and flag:
            path = "/home/lexa/SortFile/file/Configuration Extension.txt" 
        elif self.radKey.isChecked() and flag:
            path = "/home/lexa/SortFile/file/Configuration Key.txt"
        else: return
        with open(path, 'w',encoding="UTF-8") as data:
            for row in range(rowPosition):
                string = self.tableWidget.item(row, 0).text() + " - " + self.tableWidget.item(row, 1).text()
                data.write(string)
                if row != rowPosition - 1: data.write("\n")
    
    def Close_Configuration(self):
        message = f'Вы уверены, что хотите закрыть таблицу без сохранения?'
        flag = CustomMessageBox().show_question_box("Подверждение", message)
        if not flag: return
        self.tableWidget.setRowCount(0)
        self.spbRow.setMaximum(1)
        self.spbRow.setValue(1)

    def Clear_txt(self):
        self.txtDir.setText("")
        self.txtDirTable.setText("")
        self.txtKey.setText("")
     
    def Get_information(self):
        import subprocess
        subprocess.run(['xdg-open', "/home/lexa/SortFile/file/Information.docx"])
    
    def Confing_Key(self):
        os.system('/bin/python3 /home/lexa/SortFile/bin/ReName.py')

    def Confing_Floders(self):
        os.system('/bin/python3 /home/lexa/SortFile/bin/Synchronization.py')
    
    def AddElems(self, string):
        if (self.txtDirTable.text() == ""):
            CustomMessageBox().show_warning_box("Ошибка!", "Укажите дирикторию!")
            return
        if (self.txtKey.text() == ""):
            CustomMessageBox().show_warning_box("Ошибка!", "Укажите ключ")
            return
        rowPosition = self.tableWidget.rowCount()                              
        self.tableWidget.insertRow(rowPosition)
        if string-1 == rowPosition:
            self.tableWidget.setItem(string-1,0,QTableWidgetItem(self.txtKey.text()))
            self.tableWidget.setItem(string-1,1,QTableWidgetItem(self.txtDirTable.text()))
            self.spbRow.setMaximum(rowPosition + 2)
        else:
            self.spbRow.setMaximum(rowPosition + 2)
            data = [[self.txtKey.text(), self.txtDirTable.text()]]
            for row in range(string-1, rowPosition):
                data.append([self.tableWidget.item(row, 0).text(), self.tableWidget.item(row, 1).text()])
            for row in range(string-1, rowPosition+1):
                self.tableWidget.setItem(row,0,QTableWidgetItem(data[row-string+1][0]))
                self.tableWidget.setItem(row,1,QTableWidgetItem(data[row-string+1][1]))
        self.spbRow.setValue(rowPosition+2)
        
    def DelElems(self, string):
        rowPosition = self.tableWidget.rowCount()
        if (rowPosition == 0):
            return
        if (string == rowPosition + 1):
            CustomMessageBox().show_warning_box("Ошибка!", "Вы не можете удалить не существующею строку!")
            return
        if (rowPosition != 1):
            self.spbRow.setMaximum(rowPosition - 1)
        self.tableWidget.removeRow(string-1)

    def Sort_files(self):
        flag = CustomMessageBox().show_question_box("Подверждение", "Вы уверены, что хотите сортировать файлы ?")
        if flag:
            start_path = self.txtDir.text()
            if start_path == '':
                CustomMessageBox().show_warning_box("Ошибка!", "Введите дирикторию для сортировки!")
                return
            else:
                flag = self.radKey.isChecked()
                Sorted_files(start_path, flag)


